function [fitresult, gof] = createFit(x, y, Intensity)
%CREATEFIT(X,Y,INTENSITY)
%  Create a fit.
%
%  Data for 'untitled fit 1' fit:
%      X Input : x
%      Y Input : y
%      Z Output: Intensity
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 27-Sep-2021 18:32:55


%% Fit: 'untitled fit 1'.
[xData, yData, zData] = prepareSurfaceData( x, y, Intensity );

% Set up fittype and options.
ft = fittype( '120.13 + amplitude*exp(-0.5*(((x-x_0)^2) + ((y-y_0)^2))/(sigma^2))', 'independent', {'x', 'y'}, 'dependent', 'z' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.StartPoint = [0.678735154857773 0.743132468124916 0.392227019534168 0.655477890177557];

% Fit model to data.
[fitresult, gof] = fit( [xData, yData], zData, ft, opts );

% Plot fit with data.
figure( 'Name', 'untitled fit 1' );
h = plot( fitresult );
legend( h, 'untitled fit 1', 'Intensity vs. x, y', 'Location', 'NorthEast', 'Interpreter', 'none' );
% Label axes
xlabel( 'x', 'Interpreter', 'none' );
ylabel( 'y', 'Interpreter', 'none' );
zlabel( 'Intensity', 'Interpreter', 'none' );
grid on


